import { Link } from "react-router-dom";

export default function Login() {

    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [validation, setValidation] = useState(false);

    const navigate = useNavigate();

    const handleSubmit = ((e) => {
        e.preventDefault();
        const loginData = {
            name, 
            password
        };

        fetch("http://localhost:8000/employees", {
            method: 'POST',
            headers: {
                "content-type": "application/json"
            },
            body: JSON.stringify(loginData)
        })
        .then((res) => {
            alert("Employee data has been saved");
            navigate("/");
        }).catch((err) => {
            console.log(err.message);
        })
    })

    return (
        <div className="container">
            <div className="subcontainer">
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="name">Name:</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        required 
                        value={name} 
                        onChange={e=>setName(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {name.length === 0 && validation && <span className="errorMsg">Please Enter Name</span>}

                    <label htmlFor="password">Password:</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password" 
                        required 
                        value={password} 
                        onChange={e=>setPlace(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {password.length === 0 && validation && <span className="errorMsg">Please Enter Place</span>}

                    <button className="btn btn-primary">Login</button>
                    <Link to="/register" className="btn btn-danger">Register</Link>
                </form>
            </div>
        </div>
    )
}
