import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";


export default function Register() {

    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [validation, setValidation] = useState(false);

    const navigate = useNavigate();

    const handleSubmit = ((e) => {
        e.preventDefault();
        const loginData = {
            name, 
            password, 
            confirmPassword
        };

        // fetch("http://localhost:3000/employees", {
        //     method: 'POST',
        //     headers: {
        //         "content-type": "application/json"
        //     },
        //     body: JSON.stringify(loginData)
        // })
        // .then((res) => {
        //     alert("Employee data has been saved");
        //     navigate("/");
        // }).catch((err) => {
        //     console.log(err.message);
        // })
    })

    return (
        <div className="container">
            <div className="subcontainer">
                <h2>Register</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="name">Name:</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        required 
                        value={name} 
                        onChange={e=>setName(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {name.length === 0 && validation && <span className="errorMsg">Please Enter Name</span>}

                    <label htmlFor="password">Password:</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password" 
                        required 
                        value={password} 
                        onChange={e=>setPassword(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {password.length === 0 && validation && <span className="errorMsg">Please Enter Password</span>}

                    <label htmlFor="confirm_password">Confirm Password:</label>
                    <input 
                        type="password" 
                        id="confirm_password" 
                        name="confirm_password" 
                        required 
                        value={confirmPassword} 
                        onChange={e=>setConfirmPassword(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {confirmPassword.length === 0 && validation && <span className="errorMsg">Please Enter Confirm Password</span>}

                    <button className="btn btn-primary" style={{width: '100%', marginBottom: '20px'}}>Register</button>
                    <br></br>
                    Already have an account?
                    <Link to="/login" className="btn btn-danger">Login</Link>
                </form>
            </div>
        </div>
    )
}
