import { useEffect, useState } from "react"
import api from "./api"
import { Link, useNavigate } from "react-router-dom";

export default function Dashboard() {
    const [products, setProducts] = useState([]);
    const [page, setPage] = useState(1);
    const [total, setTotal] = useState(0);
    const limit = 5; // items per page
    const navigate = useNavigate();

    const token = localStorage.getItem("token");


    const handleEdit = (id) => {
        navigate("/edit/" + id);
    }

    const handleDelete = async (id) => {
        if(window.confirm("Are you sure you want to delete?")){
            try {
                const res = await api.delete(`/products/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                // Update product list without reloading
                setProducts((prev) => prev.filter((item) => item._id !== id));
                alert("Product data has been removed");
            } catch (err) {
                console.log(err.message);
            }
        }
    }

    useEffect(() => {
        const checkAuthAndFetch = async () => {
            const token = localStorage.getItem("token");
            if (!token) {
                navigate("/login");
                return;
            }

            try {
                const res = await api.get("/products", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                // console.log(res.data);
                setProducts(res.data);

            } catch (err) {
                console.error("Invalid token or error:", err);
                navigate("/login");
            }
        };

        checkAuthAndFetch();
    }, [navigate, page]);

    return (
        <div className="container">
        <div className="subcontainer">
            <h2>Product Record</h2>
            <div className="table-container">
                <Link to="/create" className="btn btn-primary">Add New Product</Link>
                <table>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>NAME</th>
                            <th>Description</th>
                            <th>ACTIONS</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                    products.length === 0 ? (
                        <tr>
                            <td colSpan="4">No products found.</td>
                        </tr>
                        ) : (
                            products.map((item, index) => (
                                <tr key={item._id}>
                                  <td>{index + 1}</td>
                                  <td>{item.name}</td>
                                  <td>{item.description}</td>
                                  <td>
                                    <button className="btn btn-secondary" onClick={() => handleEdit(item._id)}>Edit</button>
                                    <button className="btn btn-danger" onClick={() => handleDelete(item._id)}>Delete</button>
                                  </td>
                                </tr>
                              ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    </div>
    )
}
