import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Login() {

    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    // const [validation, setValidation] = useState(false);

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post("/auth/login", { name, password });
            const token = res.data.token;

            // Save token locally
            localStorage.setItem("token", token);

            // Set default auth header for future requests
            axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

            // Redirect
            navigate("/");
          } catch (err) {
            console.log(err.message);
            alert("Invalid credentials");
          }
    }


    return (
        <div className="container">
            <div className="subcontainer">
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="name">Name:</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        required 
                        value={name} 
                        onChange={e=>setName(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {name.length === 0 && validation && <span className="errorMsg">Please Enter Name</span>}

                    <label htmlFor="password">Password:</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password" 
                        required 
                        value={password} 
                        onChange={e=>setPassword(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {password.length === 0 && validation && <span className="errorMsg">Please Enter Password</span>}

                    <button className="btn btn-primary">Login</button>
                    <Link to="/register" className="btn btn-danger">Register</Link>
                </form>
            </div>
        </div>
    )
}
