            // Save token locally
            localStorage.setItem("token", token);
            // Set default auth header for future requests
            api.defaults.headers.common["Authorization"] = `Bearer ${token}`;

            // Redirect
            navigate("/");
        } catch (err) {
            console.log(err.message);
            alert("Invalid credentials");
        }
    }

    return (
        <div className="container">
            <div className="subcontainer">
                <h2>Register</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="name">Name:</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        required 
                        value={name} 
                        onChange={e=>setName(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {name.length === 0 && validation && <span className="errorMsg">Please Enter Name</span>}

                    <label htmlFor="password">Password:</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password" 
                        required 
                        value={password} 
                        onChange={e=>setPassword(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {password.length === 0 && validation && <span className="errorMsg">Please Enter Password</span>}

                    <label htmlFor="confirm_password">Confirm Password:</label>
                    <input 
                        type="password" 
                        id="confirm_password" 
                        name="confirm_password" 
                        required 
                        value={confirmPassword} 
                        onChange={e=>setConfirmPassword(e.target.value)}
                        onMouseDown={()=>setValidation(true)}
                    />
                    {confirmPassword.length === 0 && validation && <span className="errorMsg">Please Enter Confirm Password</span>}

                    <button className="btn btn-primary" style={{width: '100%', marginBottom: '20px'}}>Register</button>
                    <br></br>
                    Already have an account?
                    <Link to="/login" className="btn btn-danger">Login</Link>
                </form>
            </div>
        </div>
    )
}
