import { useEffect, useState } from "react"
import api from "./api"
import { Link, useNavigate } from "react-router-dom";

export default function Dashboard() {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    const token = localStorage.getItem("token");


    const EditDetails = (id) => {
        navigate("/edit/" + id);
    }

    const DeleteDetails = async (id) => {
        if(window.confirm("Are you sure you want to delete?")){
            try {
                const token = localStorage.getItem("token");
                const res = await api.delete(`/products/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                alert("Employee data has been removed");
                window.location.reload();
            } catch (err) {
                console.log(err.message);
            }
        }
    }

    useEffect(() => {
        const checkAuth = async () => {
            const token = localStorage.getItem("token");
            if (!token) {
                navigate("/login");
                return;
            }

            try {
                const res = await api.get("/products", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setProducts(res.data);

                console.log("Product data:", res.data);
            } catch (err) {
                console.error("Invalid token or error:", err);
                navigate("/login");
            }
        };

        checkAuth();
    }, [navigate]);

    return (
        <div className="container">
        <div className="subcontainer">
            <h2>Product Record</h2>
            <div className="table-container">
                <Link to="/create" className="btn btn-primary">Add New Product</Link>
                <table>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>NAME</th>
                            <th>Description</th>
                            <th>ACTIONS</th>
                        </tr>
                    </thead>
                    <tbody>
                    <>
                    {
                        products && products.map((item, index) => {
                            return (
                                <tr key={item._id}>
                                    <td>{index + 1}</td>
                                    <td>{item.name}</td>
                                    <td>{item.description}</td>
                                    <td>
                                        <button className="btn btn-secondary" onClick={() => EditDetails(item._id)}>Edit</button>
                                        <button className="btn btn-danger" onClick={() => DeleteDetails(item._id)}>Delete</button>
                                    </td>
                                </tr>
                            );
                    })}
                    </>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
    )
}
